import React from "react";

export default function App() {
  const sections = [
    {
      title: "Let's Start Learning",
      description: "Jump into our reading-assistance app and enhance your skills.",
      buttonText: "Start Learning",
      href: "/exercise",
    },
    {
      title: "Quiz Section",
      description: "Test your knowledge and track your progress with fun quizzes.",
      buttonText: "Take a Quiz",
      href: "#",
    },
    {
      title: "Community Hub",
      description: "Share your achievements and connect with fellow learners.",
      buttonText: "Join Community",
      href: "#",
    },
    {
      title: "Expert Mentors",
      description: "Get personalized guidance from our expert mentors for $100/month.",
      buttonText: "Find a Mentor",
      href: "#",
    },
  ];

  const containerStyle = {
    minHeight: "100vh",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    background: "linear-gradient(to bottom right, #f8fafc, #e2e8f0)",
    fontFamily: "Arial, sans-serif",
    color: "#111",
  };

  const headerStyle = {
    marginBottom: "60px",
    textAlign: "center",
  };

  const titleStyle = {
    fontSize: "64px",
    fontWeight: "bold",
    background: "linear-gradient(to right, #2563eb, #7c3aed, #f59e0b)",
    WebkitBackgroundClip: "text",
    WebkitTextFillColor: "transparent",
  };

  const subtitleStyle = {
    marginTop: "16px",
    fontSize: "20px",
    color: "#555",
  };

  const gridStyle = {
    display: "grid",
    gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
    gap: "20px",
    width: "100%",
    maxWidth: "1200px",
    padding: "20px",
  };

  const cardStyle = {
    border: "1px solid #ddd",
    borderRadius: "12px",
    padding: "20px",
    background: "#fff",
    boxShadow: "0 2px 6px rgba(0,0,0,0.1)",
    transition: "transform 0.3s, box-shadow 0.3s",
  };

  const cardHover = {
    transform: "translateY(-5px)",
    boxShadow: "0 6px 12px rgba(0,0,0,0.15)",
  };

  const cardTitle = {
    fontSize: "20px",
    fontWeight: "600",
    marginBottom: "10px",
  };

  const cardDesc = {
    fontSize: "16px",
    marginBottom: "20px",
    color: "#444",
  };

  const buttonStyle = {
    display: "inline-block",
    width: "100%",
    padding: "10px",
    backgroundColor: "#2563eb",
    color: "#fff",
    textAlign: "center",
    borderRadius: "6px",
    textDecoration: "none",
    transition: "background 0.3s",
  };

  const buttonHover = {
    backgroundColor: "#1d4ed8",
  };

  const footerStyle = {
    marginTop: "40px",
    padding: "20px",
    color: "#666",
    fontSize: "14px",
  };

  return (
    <div style={containerStyle}>
      <main style={{ flex: 1, display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center" }}>
        <div style={headerStyle}>
          <h1 style={titleStyle}>DysleXa</h1>
          <p style={subtitleStyle}>Your AI-powered companion for joyful and confident reading.</p>
        </div>

        <div style={gridStyle}>
          {sections.map((section, idx) => (
            <div
              key={idx}
              style={cardStyle}
              onMouseEnter={(e) => Object.assign(e.currentTarget.style, cardHover)}
              onMouseLeave={(e) => Object.assign(e.currentTarget.style, cardStyle)}
            >
              <h2 style={cardTitle}>{section.title}</h2>
              <p style={cardDesc}>{section.description}</p>
              <a
                href={section.href}
                style={buttonStyle}
                onMouseEnter={(e) => Object.assign(e.currentTarget.style, buttonHover)}
                onMouseLeave={(e) => Object.assign(e.currentTarget.style, buttonStyle)}
              >
                {section.buttonText}
              </a>
            </div>
          ))}
        </div>
      </main>

      <footer style={footerStyle}>
        <p>&copy; {new Date().getFullYear()} DysleXa. All rights reserved.</p>
      </footer>
    </div>
  );
}
